mapper.select:
SELECT id, plan_id, day_index, title, plan_date
FROM plan_days
WHERE id = #{id}

mapper.update:
UPDATE plan_days
SET plan_id = #{planId},
    day_index = #{dayIndex},
    title = #{title},
    plan_date = #{planDate}
WHERE id = #{id}

mapper.select:
SELECT id, plan_id, day_index, title, plan_date
FROM plan_days
WHERE plan_id = #{planId} AND day_index = #{dayIndex}

SELECT MAX(day_index)
FROM plan_days
WHERE plan_id = #{planId}
