mapper.update:
UPDATE sns_tokens
SET user_access_token = #{userAccessToken}, page_access_token = #{pageAcessToken}, expires_at = #{expiresAt},
    account_type = #{accountType}, ig_business_account = #{igBusinessId},creator_account =#{creatorAccount}, publish_account=#{publishAccount}, created_at = #{createdAt}, user_id = #{userId}
WHERE id = #{id}
